<script>
import x0p from 'x0popup';
import 'x0popup/dist/x0popup.min.css';


export default {
    name: "DocsNav",
    props: {
        content: {
            type: String,
            default: 'Scott Editor Engine',
        },
        subContent: {
            type: String,
            default: 'Useful, lightweight and powerful',
        },
        indexIsInExample: {
            type: String,
            default: 'false',
        },
        md: {
            type: String,
            default: '',
        },
    },
    data() {
        return {
            toggleDropMenu: false,
        };
    },
    methods: {
        clearCache() {
            let that = this;
            // insert code here.
            x0p({
                title: '确认',
                text: '确认清除缓存？此操作无法撤销！',
                buttons: [
                    {
                        type: 'cancel',
                        text: '取消',
                    },
                    {
                        type: 'warning',
                        text: '确认',
                    },
                ]
            }).then(function (data, state) {
                if(data.button == 'warning') {
                    that.$emit('clearContent');
                    localStorage.removeItem("scottEditorEngineContentCache");
                    localStorage.removeItem("cacheEditTime");
                    console.log("清除缓存成功！");
                    that.$store.commit('alertTips', ['success', "清除缓存成功！"]);
                }
                if(data.button == 'cancel') {
                    console.log("用户取消提交操作。");
                }
            });
        },
    },
}
</script>

